    <div class="b-slider__product-list">
        <div class="swiper-container  b-slider__swiper-container   js-b-slider__swiper-container ">
            <div class="swiper-wrapper   b-slider__swiper-wrapper">
                @foreach(range(0,25) as $item)
                <div class="swiper-slide b-slider__product-item">
                    @include('frontend.global.parts.product_item')
                </div>
                @endforeach
            </div>
        </div>



    </div>
